import React from "react";
import { SimpleGrid, Card, CardBody,CardBodyProps,Text,CardHeader, Image, CardFooter, Box, Heading, Flex, Spacer } from "@chakra-ui/react";
import { Product } from "./Product";
import { productList } from "../../products";

interface ProductProps {
    products: Product[];
}

export function getProducts(products: Product[]): Product[] {
    const productCopy = products.map((productData: Product): Product => ({...productData}));
    return productCopy;
}

const productArray = getProducts(productList);

export function productProperty(props: ProductProps){
    const productList = props;
}

export function Products(): JSX.Element {

    const productArray = getProducts(productList);

    function expandArray(array: string[]):string{
        const copy = [...array];
        const listOfItems = copy.join(", ");
        return listOfItems;
    }

    return(
    // <div>movies</div>
        <SimpleGrid w="200vh" h="400vh" p="4vh"  spacing = {2} templateColumns={{base: "repeat(4, 1fr)"}}>
            {productList.map((product)=>(
                <Card maxW="md" direction={{base: "row", sm:"column"}} overflow="hidden" variant="elevated" key={product.name}>
                    <CardHeader key={product.name}>
                        <Box>
                            <Heading size="sm">
                                {product.name}
                            </Heading>
                        </Box>
                    </CardHeader>
                    <Box>
                        <CardBody>
                            <Flex>
                                <Image maxW={{base: "100%", sm:"100px"}} src={product.image} alt="poster"></Image>
                                <Spacer></Spacer>
                                <Text fontSize="xs" key={product.description}>{product.description}</Text>
                                <Text fontSize="xs">                                
                                    <span>Cost: {product.avgCost}</span>
                                    <div></div>
                                    <span>Usage: {product.usage}</span>
                                    <div></div>
                                    <span>Material: {product.material}</span>
                                </Text>
                            </Flex>
                        </CardBody>
                    </Box>
                </Card>
            ))}
        </SimpleGrid>
    );
}
